    test("venue reader") {
        val json = """{
            "city":"Boston",
            "country":"United States",
            "region":"MA",
            "postal_code":"75000",
            "address_2":"line 2 of address",
            "address":"line 1 of address"
        }"""

        val venue: Venue = venueReader.reads(Json.parse(json)) match {
            case JsSuccess(venue, _) => venue
            case JsError(errors) => fail("pasing EventbriteEvent failed !!!")
        }

        venue.address.get should be("line 1 of address")
        venue.address_2.get should be("line 2 of address")
        venue.postal_code.get should be("75000")
        venue.region.get should be("MA")
        venue.country.get should be("United States")
        venue.city.get should be("Boston")
    }

