    test("eventbriteResponseReader with summary") {
        val json = """{ "events": [
             {
                 "summary": {
                     "total_items": 41
                 }
             },
             {
                "event":{
                    "id":4819205373,
                    "title":"toto"
                }
            }
        ] }"""

        val events: Seq[EventbriteEvent] = eventbriteResponseReader.reads(Json.parse(json)) match {
            case JsSuccess(event, _) => event.asOpt.getOrElse(Nil)
            case JsError(errors) => fail("pasing EventbriteEvent failed !!!")
        }

        events should have size(1)

        val event = events.head
        event.id should be(Option("4819205373"))
        event.title should be(Option("toto"))
    }
