package api.eventbrite

import org.scalatest.matchers.ShouldMatchers
import org.scalatest.FunSuite
import org.scalatest.BeforeAndAfter
import java.net.URL
import com.codahale.jerkson.Json
import java.io.{InputStreamReader, BufferedReader}
import org.codehaus.jackson.annotate.JsonIgnoreProperties
import org.joda.time.DateTime

class EventbriteTest extends FunSuite with ShouldMatchers with BeforeAndAfter with Json {
    val eventbriteKey = "2Z5MEY5C4CD7D3FXUA"
    val url = "https://www.eventbrite.com/json/event_search?app_key=" + eventbriteKey + "&keywords=scala"
    val stream = new URL(url).openStream()
    val json = new BufferedReader(new InputStreamReader( stream )).readLine()

    test("should have something from eventbrite") {
        json.length should be > 1
    }

    test("should map 1 dummy event") {
        val events: Events = parse[Events]("""{"events":[ {"event": { "id":4737033595 } } ] }""")
        events.events.size should be (1)
    }

    test("should map event") {
        val json = """{
             "event":{
                 "id":4819205373,
                 "title":"Typesafe's Fast Track to Scala",
                 "start_date":"2013-01-28 09:00:00",
                 "end_date":"2013-01-29 17:00:00",
                 "description":"<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: x-large;\">Summary</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><A HREF=\"http://www.composables.com\" TARGET=\"_blank\">Composable Solutions</A>, in partnership with <A HREF=\"http://www.typesafe.com\" TARGET=\"_blank\">Typesafe</A>, is pleased to offer a two-day course, designed by Martin Odersky, the creator of the Scala programming language, and Heiko Seeberger, a recognized Scala expert, with a goal of giving you an excellent grounding in Scala.</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">It is intended to enable developers or development managers, who are experienced programmers in Java or other production languages like C++, C# or Ruby, to confidently start programming in Scala. No previous knowledge of Scala is assumed. Although intense, the course ensures you will have a solid understanding of the fundamentals of the language, the tooling and the development process as well as a good appreciation of the more advanced features. If you already have Scala programming experience, then this course could be a useful refresher.</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><BR></SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: x-large;\">Objectives</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">After having participated in this course you should:</SPAN></P>\r\n<UL>\r\n<LI><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">be a competent user of Scala constructs in application code</SPAN></LI>\r\n<LI><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">know and be able to apply the functional programming style in Scala</SPAN></LI>\r\n<LI><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">know how to use the fundamental Scala tools</SPAN></LI>\r\n<LI><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">be confident to start using Scala in production applications</SPAN></LI>\r\n</UL>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><SPAN STYLE=\"font-size: x-large;\"><BR></SPAN></SPAN></P>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><SPAN STYLE=\"font-size: x-large;\">Prerequisites</SPAN>\u00a0</SPAN></P>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">The presentation will frequently be mixed with hands-on exercises that give you a good opportunity to try what you have learnt and a chance to clarify your understanding. The training facility can provide a certain number of workstations, but we highly recommend bringing your laptop so that you can preserve a fully set up Scala development environment after the course ends.</SPAN></P>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><BR></SPAN></P>\r\n<P STYLE=\"text-align: -webkit-auto;\"><SPAN STYLE=\"font-size: x-large; color: #003366;\">Instructors</SPAN><SPAN STYLE=\"color: #003366; font-family: tahoma, arial, helvetica, sans-serif; line-height: 21px; font-size: medium;\">\u00a0</SPAN>\u00a0</P>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><SPAN STYLE=\"line-height: 18px;\"><SPAN STYLE=\"font-size: large;\"><STRONG>Brian Clapper</STRONG></SPAN> has nearly 30 years' experience building and maintaining software systems and has been a consultant since 2009. Brian has a broad base of experience, from financial trading systems and master data management software to ecommerce web sites and Internet advertising software. He is comfortable working in companies of all sizes, but he has a special fondness for high-energy startups. He knows, and regularly uses, many different programming languages, including Scala, Python, Ruby, Java, C and C#. (He likes to think of himself as a generalist and a polyglot.) Brian is founder of the\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://scala-phase.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Philadelphia Area Scala Enthusiasts</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(PHASE). He is co-organizer of\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://scalathon.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Scalathon</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">, the international Scala hackathon, and he has been a co-organizer of the annual\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://nescala.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Northeast Scala Symposium</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0since it began, in 2011. Brian maintains a number of open-source Scala projects, including\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/argot/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Argot</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(a command line parser),\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/avsl/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">AVSL</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(an\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://www.slf4j.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">SLF4J</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">-based logging framework),\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/grizzled-scala/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Grizzled Scala</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(a general purpose library),\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/classutil/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">ClassUtil</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(a fast class-finder library),\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/sqlshell/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">SQLShell</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(a JDBC-based SQL command shell), and several\u00a0</SPAN><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://www.scala-sbt.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #003366;\">SBT</SPAN></A><SPAN STYLE=\"line-height: 18px;\">\u00a0plugins. All are available at\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">software.<SPAN CLASS=\"il\">clapper</SPAN>.org</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0or in Brian's\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"https://github.com/bmc/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">GitHub repository</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">.</SPAN></SPAN></P>\r\n<P STYLE=\"text-align: -webkit-auto;\"><FONT SIZE=\"3\" COLOR=\"#003366\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 18px;\"><BR></SPAN></FONT></P>\r\n<P STYLE=\"color: #000000; font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: normal; text-align: left;\"><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><SPAN STYLE=\"line-height: 18px;\"><SPAN STYLE=\"font-size: large;\"><STRONG>Nermin Serifovic</STRONG></SPAN> has 10 years of experience building enterprise software applications using Java technologies. For the past 5 years he has focused on architecting, designing and developing backend platforms. Nermin has been a Scala enthusiast since 2009 and practitioning it professionally since mid 2011. He has given various talks at JavaOne, <A HREF=\"http://days2011.scala-lang.org/node/138/293\" TARGET=\"_blank\">Scala Days</A> and <A HREF=\"http://vimeo.com/20290504\" TARGET=\"_blank\">Northeast Scala Symposium</A>, as well as at local Scala and Java user groups. Nermin is a founder and co-organizer of\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://www.meetup.com/boston-scala/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Boston Area Scala Enthusiasts</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0user group and co-founder and co-organizer of\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://nescala.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Northeast Scala Symposium</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">. Furthermore, he is a co-creator and co-maintainer of the\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://www.scalapuzzlers.com/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Scala Puzzlers website</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">. Nermin holds a MEng in Computer Science from Cornell University and his areas of interest include distributed systems along with concurrent, parallel and functional programming.</SPAN></SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">\u00a0</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: x-large;\">Outline</SPAN></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">Introduction</SPAN></STRONG></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">First steps in Scala</SPAN></STRONG></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">OO basics</SPAN></STRONG></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">Testing</SPAN></STRONG></P>\r\n<P><FONT SIZE=\"3\" COLOR=\"#003366\" FACE=\"tahoma, arial, helvetica, sans-serif\"><STRONG>Collections and functional programming</STRONG></FONT></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">For-expressions and loops</SPAN></STRONG></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><STRONG>Inheritance and traits</STRONG>\u00a0</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><STRONG>Pattern Matching</STRONG>\u00a0</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><STRONG>Dealing with optional values</STRONG>\u00a0</SPAN></P>\r\n<DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\"><BR></SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">Cancellation Policy</SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\"><BR></SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">This course is offered contigent upon minimum enrollment requirements. Composable Solutions reserves the right to cancel the course in case of a low enrollment, not later than three weeks before beginning of the course. You will receive a full refund if the course is cancelled.</SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\"><BR></SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">Students who need to cancel the attendance will receive (less any registration servicing fees):</SPAN></FONT></SPAN></DIV>\r\n<DIV>\r\n<UL>\r\n<LI><SPAN STYLE=\"color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">Full refund with two weeks notice</SPAN></FONT></SPAN></LI>\r\n<LI><SPAN STYLE=\"color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">80% refund with one week notice</SPAN></FONT></SPAN></LI>\r\n<LI><SPAN STYLE=\"color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">25% refund with less than one week notice</SPAN></FONT></SPAN></LI>\r\n</UL>\r\n</DIV>\r\n</DIV>",
                 "tags":"Scala training",
                 "url":"http://www.eventbrite.com/event/4819205373/SRCH",
                 "timezone_offset":"GMT-0500"
             }
        }"""
        val event: EventTag = parse[EventTag](json)
        event.event.id.get should be ("4819205373")
        event.event.title.get should be ("Typesafe's Fast Track to Scala")
        event.event.start_date.get should be ("2013-01-28 09:00:00")
        event.event.end_date.get should be ("2013-01-29 17:00:00")
        event.event.description.get should be ("<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: x-large;\">Summary</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><A HREF=\"http://www.composables.com\" TARGET=\"_blank\">Composable Solutions</A>, in partnership with <A HREF=\"http://www.typesafe.com\" TARGET=\"_blank\">Typesafe</A>, is pleased to offer a two-day course, designed by Martin Odersky, the creator of the Scala programming language, and Heiko Seeberger, a recognized Scala expert, with a goal of giving you an excellent grounding in Scala.</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">It is intended to enable developers or development managers, who are experienced programmers in Java or other production languages like C++, C# or Ruby, to confidently start programming in Scala. No previous knowledge of Scala is assumed. Although intense, the course ensures you will have a solid understanding of the fundamentals of the language, the tooling and the development process as well as a good appreciation of the more advanced features. If you already have Scala programming experience, then this course could be a useful refresher.</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><BR></SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: x-large;\">Objectives</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">After having participated in this course you should:</SPAN></P>\r\n<UL>\r\n<LI><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">be a competent user of Scala constructs in application code</SPAN></LI>\r\n<LI><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">know and be able to apply the functional programming style in Scala</SPAN></LI>\r\n<LI><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">know how to use the fundamental Scala tools</SPAN></LI>\r\n<LI><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">be confident to start using Scala in production applications</SPAN></LI>\r\n</UL>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><SPAN STYLE=\"font-size: x-large;\"><BR></SPAN></SPAN></P>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><SPAN STYLE=\"font-size: x-large;\">Prerequisites</SPAN>\u00a0</SPAN></P>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">The presentation will frequently be mixed with hands-on exercises that give you a good opportunity to try what you have learnt and a chance to clarify your understanding. The training facility can provide a certain number of workstations, but we highly recommend bringing your laptop so that you can preserve a fully set up Scala development environment after the course ends.</SPAN></P>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><BR></SPAN></P>\r\n<P STYLE=\"text-align: -webkit-auto;\"><SPAN STYLE=\"font-size: x-large; color: #003366;\">Instructors</SPAN><SPAN STYLE=\"color: #003366; font-family: tahoma, arial, helvetica, sans-serif; line-height: 21px; font-size: medium;\">\u00a0</SPAN>\u00a0</P>\r\n<P><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><SPAN STYLE=\"line-height: 18px;\"><SPAN STYLE=\"font-size: large;\"><STRONG>Brian Clapper</STRONG></SPAN> has nearly 30 years' experience building and maintaining software systems and has been a consultant since 2009. Brian has a broad base of experience, from financial trading systems and master data management software to ecommerce web sites and Internet advertising software. He is comfortable working in companies of all sizes, but he has a special fondness for high-energy startups. He knows, and regularly uses, many different programming languages, including Scala, Python, Ruby, Java, C and C#. (He likes to think of himself as a generalist and a polyglot.) Brian is founder of the\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://scala-phase.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Philadelphia Area Scala Enthusiasts</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(PHASE). He is co-organizer of\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://scalathon.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Scalathon</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">, the international Scala hackathon, and he has been a co-organizer of the annual\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://nescala.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Northeast Scala Symposium</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0since it began, in 2011. Brian maintains a number of open-source Scala projects, including\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/argot/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Argot</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(a command line parser),\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/avsl/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">AVSL</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(an\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://www.slf4j.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">SLF4J</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">-based logging framework),\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/grizzled-scala/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Grizzled Scala</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(a general purpose library),\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/classutil/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">ClassUtil</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(a fast class-finder library),\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/sqlshell/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">SQLShell</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0(a JDBC-based SQL command shell), and several\u00a0</SPAN><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://www.scala-sbt.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #003366;\">SBT</SPAN></A><SPAN STYLE=\"line-height: 18px;\">\u00a0plugins. All are available at\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://software.clapper.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">software.<SPAN CLASS=\"il\">clapper</SPAN>.org</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0or in Brian's\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"https://github.com/bmc/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">GitHub repository</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">.</SPAN></SPAN></P>\r\n<P STYLE=\"text-align: -webkit-auto;\"><FONT SIZE=\"3\" COLOR=\"#003366\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 18px;\"><BR></SPAN></FONT></P>\r\n<P STYLE=\"color: #000000; font-family: Helvetica, Arial, sans-serif; font-size: 12px; line-height: normal; text-align: left;\"><SPAN STYLE=\"line-height: 21px; text-align: -webkit-auto; font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><SPAN STYLE=\"line-height: 18px;\"><SPAN STYLE=\"font-size: large;\"><STRONG>Nermin Serifovic</STRONG></SPAN> has 10 years of experience building enterprise software applications using Java technologies. For the past 5 years he has focused on architecting, designing and developing backend platforms. Nermin has been a Scala enthusiast since 2009 and practitioning it professionally since mid 2011. He has given various talks at JavaOne, <A HREF=\"http://days2011.scala-lang.org/node/138/293\" TARGET=\"_blank\">Scala Days</A> and <A HREF=\"http://vimeo.com/20290504\" TARGET=\"_blank\">Northeast Scala Symposium</A>, as well as at local Scala and Java user groups. Nermin is a founder and co-organizer of\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://www.meetup.com/boston-scala/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Boston Area Scala Enthusiasts</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">\u00a0user group and co-founder and co-organizer of\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://nescala.org/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Northeast Scala Symposium</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">. Furthermore, he is a co-creator and co-maintainer of the\u00a0</SPAN><SPAN STYLE=\"color: #ff6600;\"><A STYLE=\"color: #0088cc; font-family: 'Open Sans', Helvetica, Arial, sans-serif; font-size: 16px; line-height: 18px;\" HREF=\"http://www.scalapuzzlers.com/\" TARGET=\"_blank\"><SPAN STYLE=\"color: #ff6600;\">Scala Puzzlers website</SPAN></A></SPAN><SPAN STYLE=\"line-height: 18px;\">. Nermin holds a MEng in Computer Science from Cornell University and his areas of interest include distributed systems along with concurrent, parallel and functional programming.</SPAN></SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">\u00a0</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: x-large;\">Outline</SPAN></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">Introduction</SPAN></STRONG></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">First steps in Scala</SPAN></STRONG></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">OO basics</SPAN></STRONG></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">Testing</SPAN></STRONG></P>\r\n<P><FONT SIZE=\"3\" COLOR=\"#003366\" FACE=\"tahoma, arial, helvetica, sans-serif\"><STRONG>Collections and functional programming</STRONG></FONT></P>\r\n<P><STRONG><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\">For-expressions and loops</SPAN></STRONG></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><STRONG>Inheritance and traits</STRONG>\u00a0</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><STRONG>Pattern Matching</STRONG>\u00a0</SPAN></P>\r\n<P><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; color: #003366; font-size: medium;\"><STRONG>Dealing with optional values</STRONG>\u00a0</SPAN></P>\r\n<DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\"><BR></SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">Cancellation Policy</SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\"><BR></SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">This course is offered contigent upon minimum enrollment requirements. Composable Solutions reserves the right to cancel the course in case of a low enrollment, not later than three weeks before beginning of the course. You will receive a full refund if the course is cancelled.</SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\"><BR></SPAN></FONT></SPAN></DIV>\r\n<DIV><SPAN STYLE=\"font-family: tahoma, arial, helvetica, sans-serif; font-size: small; color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">Students who need to cancel the attendance will receive (less any registration servicing fees):</SPAN></FONT></SPAN></DIV>\r\n<DIV>\r\n<UL>\r\n<LI><SPAN STYLE=\"color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">Full refund with two weeks notice</SPAN></FONT></SPAN></LI>\r\n<LI><SPAN STYLE=\"color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">80% refund with one week notice</SPAN></FONT></SPAN></LI>\r\n<LI><SPAN STYLE=\"color: #000080;\"><FONT SIZE=\"3\" COLOR=\"#000080\" FACE=\"tahoma, arial, helvetica, sans-serif\"><SPAN STYLE=\"line-height: 20px;\">25% refund with less than one week notice</SPAN></FONT></SPAN></LI>\r\n</UL>\r\n</DIV>\r\n</DIV>")
